public class SaleItem {
	public String Item;
	public String Cost;
	public String Quan;

	public SaleItem(String item, String cost, String quan) {
		Item = item;
		Cost = cost;
		Quan = quan;
	}

	public String getItem() {
		return Item;
	}

	public String getCost() {
		return Cost;
	}

	public String getQuan() {
		return Quan;
	}

}
